INSERT INTO `tbl_madrasa_porichiti` (`madrasa_id`, `madrasa_title`, `madrasa_description`, `madrasa_image`) VALUES
(3, 'জামিয়াতুল ইসলামিয়া দারুল উলুম মাদানীনগর', 'একটি বেসরকারী ধর্মীয় শিক্ষা প্রতিষ্ঠান। আল্লাহ তা’আলার অপার অনুগ্রহে \r\nজামিয়ার ভিত্তি তাকওয়ার উপর প্রতিষ্ঠিত হয়েছে এবং জামিয়া তাকওয়ার \r\nদুগ্ধ দ্বারা লালিত হয়েছে। জামিয়া এমন কতিপয় লক্ষ্য-উদ্দেশ্য সামনে রেখে\r\n প্রতিষ্ঠিত হয়েছে যেগুলো সূচনা সূচনা পূর্ব থেকেই বাস্তবায়ন করে আসছে। \r\nআল্লাহ তা’আলার তাওফিকে নিজ লক্ষ্য-উদ্দেশ্য বাস্তবায়নে জামিয়া এ পর্যন্ত\r\n উল্লেখযোগ্য সাফল্য অর্জন করেছে। এর জন্য সকল কৃতিত্ব ও প্রশংসা আল্লাহ \r\nতা’আলার।<br>\r\nপ্রভু মোদের প্রশংসা, নেয়ামত, দান সব তো তোমার প্রশংসায় ও মহিমায় কেউ তো নয় বড় তোমার,<br>\r\nজামিয়া দুই দশকেরও কম সময়ে এমন অবদান রেখেছে যা বিরল ও আঙ্গুল দিয়ে \r\nনির্দেশ করার মত, যা চিরকাল সাফল্যের পূর্ব লক্ষণ সফলতার পূর্বাভাস হয়ে \r\nথাকবে। বস্তুত, জামিয়া নিরব নৈঃশব্দে এক অবিরাম জিহাদে ব্রতী রয়েছে। এ \r\nপথে সে, সব ধরনের কষ্ট-ক্লান্তি হাসি মুখে বরন করেছে।<br>\r\nআল-জামিয়াতুল ইসলামিয়া দারুল উলুম মাদানী নগর, বাংলাদেশের বেসরকারী বৃহত \r\nধর্মীয় শিক্ষা প্রতিষ্ঠান সমুহের অন্যতম। তার কতিপয় বিশেষ বৈশিষ্ট \r\nরয়েছে, যা সহজেই তাকে অন্য অনেক মাদ্রাসা থেকে পৃথক পরিচয়ে পরিচিতি দান \r\nকরে। সে সব বৈশিষ্টের মধ্যে সম্ভবত প্রথম বৈশিষ্ট হলো, জামিয়া তার \r\nসান্তানদেরকে স্বচ্ছ ইসলামী তরবিয়াতের আলোকে গড়ে তোলে। বস্তুত, জামিয়া \r\nআলিম (?) তৈরীর পূর্বে ‘মানুষ’ নির্মাণের উপর জোর দেয়, এবং দেশ ব্যাপী \r\nহাকডাক সম্পন্ন ব্যক্তি জন্ম দেয়ার পূর্বে মানব কাঠামোর কিছু মৌলিক উপাদান\r\n তৈরীর প্রতি গুরুত্ব দেয়। জামিয়ার এই বৈশিষ্ট নিকট ও দূরের সবার কাছে \r\nস্বীকৃত। আর এসবই হয়েছে প্রথমত আল্লাহ তা’আলার বিশেষ অনুগ্রহে, দ্বিতীয়ত \r\nসে সব ক্লান্তিকর চেষ্টা ও অবিরাম সাধনার বদৌলতে, যা জামিয়ার পরলোকগত \r\nপ্রতিষ্ঠাতা ইখলাস ও ছাওয়াব প্রাপ্তির আশায় এ পথে ব্যয় করেছেন।', './image/madrasa_image/14url.jpg'),
(4, 'অবস্থান :', 'দারুল উলুম মাদানী নগর, রাজধানী ঢাকার জিরোপয়েন্ট থেকে সাড়ে এগার\r\n কিঃ মিঃ &nbsp; দূরুত্বে দক্ষিণ-পূর্ব কোণে অবস্থিত। ঢাকা –চট্রগ্রাম \r\nবিশ্বরোডের উত্তর পার্শে জামিয়ার অবস্থান।', NULL),
(5, 'জামিয়ার প্রতিষ্ঠা :', 'জামিয়া ৭ শাওয়াল ১৪০৪ হিজরী মোতাবিক ৩১ শে ডিসেম্বর ১৯৮৪ ঈসায়ীতে \r\nপ্রতিষ্ঠিত হয়। জমিয়েতে উলামায়ে হিন্দ প্রধান, আল-জামিয়াতুল ইসলামিয়া \r\nদারুল উলুম দেওবন্দ (ভারত)-এর পরামর্শসভার মহামান্য চেয়ারম্যান, আল্লাহ \r\nওয়ালা বিশিষ্ট আলেমে দ্বীন, ফিদায়ে মিল্লাত ( জাতির স্বার্থে উতসর্গীত) \r\nসাইয়্যেদ আসআদ আল্‌ মাদানী (দা. বা.) জামিয়ার ভিত্তি প্রস্তর করেন।', NULL);

