INSERT INTO `tbl_protisthata_porichiti` (`protisthata_id`, `protisthata_title`, `protisthata_description`, `protisthata_image`) VALUES
(1, 'জামিয়াতুল ইসলামিয়া দারুল উলুম মাদানী নগর এর প্রতিষ্ঠাতা', 'জামিয়ার প্রতিষ্ঠাতা, মুসলিহে উম্মত, আলিমে রব্বানী শায়খ ইদ্রিস সন্দিপী \r\n(রহঃ) ছিলেন বাংলাদেশের সে সমস্ত নিবেদিত আলিম দ্বীনের পুন্যবান দাঈদের \r\nঅন্তর্ভূক্ত, যাঁরা যৎসামান্য আহার্য ও জীর্ণশীর্ণ বস্ত্রখন্ডের প্রতি \r\nতুষ্ট আল্লাহ তা’আলার বাণী সুউচ্চ করা এবং রাসুলে সাল্লাল্লাহু আলাইহি \r\nওয়াসাল্লাম-এর সুন্নতের পূনর্জীবনের জন্য নিজেদের জীবনকে জীবনকে ওয়াকফ \r\nকরে ছিলেন। তিনি বহু মাদরাসা প্রতিষ্ঠা করেছেন। বহু মাদরাসা প্রতিষ্ঠায় \r\nঅংশগ্রহন করেছেন। এভাবে তিনি তা’লীম তা’লীম (শিক্ষা), তরবিয়ত (দীক্ষা) এবং\r\n সম্ভাব্য সকল সযোগীতার মাধ্যমে শতাধিক মাদরাসার পৃষ্ঠপোষকতা করেছেন। সে \r\nসমস্ত মাদরাসাগুলোর মধ্যে অধিকাংশ মাদরাসাতেই জামে মসজিদ ও হিফযুল কুরআনের \r\nসতন্ত্র বিভাগ রয়েছে। তিনি হাজার হাজার আলিম জন্ম দিয়েছেন। অসংখ্য-অগণিত \r\nমানুষকে হেদায়েতের পথ প্রদর্শন করেছেন। লক্ষাধিক মানুষ তাঁর হাতে হাত রেখে\r\n নিজেদের অতীত গুনাহ থেকে তাওবা করেছে। তিনি ছিলেন অন্তর্দৃষ্টিসম্পন্ন ও \r\nসুগভীর বিবেচনা শক্তির অধিকারী। বিদআত ও কুসংস্কারের প্রতিক রূপে পরিচিত \r\nতথাকথিত বহু আলিমদের সাথে তিনি বিতর্ক করেছেন। নিঃসন্দেহে তিনি সে সমস্ত \r\nসৌভাগ্যবান মানুষের অন্তর্ভুক্ত, যাঁদের ব্যাপারে কুরআন পবিত্র কুরআনে \r\nইরশাদ হয়েছে-                من المؤمنين رجال صدقوا ما عاهدوا الله عليه,\r\n فمنهم من قضى نحبه, ومنهم من ينتظر, وما بدلوا تبديلا.<br>\r\nঅর্থঃ মু’মিনদের মাঝে এমন কিছু লোক রয়েছে যারা আল্লাহর সাথে কৃত অঙ্গীকার \r\nপূর্ণ করেছে, তাদের একদল তো জীবনায়ু পূর্ণ করেছে, আর একদল অপেক্ষায় \r\nরয়েছে। তারা তাদের কোন অঙ্গিকার পরিবর্তন করেনা। ( সুরা আহযাব-২৩)', './image/madrasa_image/(R)niceblue.jpg'),
(3, 'জন্মঃ', '<strong></strong>তিনি বর্তমান চট্রগ্রাম জেলাধীন সন্ধীপ থানার সন্তোষপুর গ্রামে ১৯৩১ সনে \r\nইসায়ীতে জন্ম গ্রহন করেন। তাঁর পিতার নাম মুন্সী আব্দুল গণী। তিনি ছিলেন \r\nছয় ভাই-বোনের মধ্যে সর্ব কনিষ্ঠ।', '');

