INSERT INTO `tbl_songbad` (`songbad_id`, `title`, `songbad`, `publication_status`) VALUES
(1, 'কুইজ প্রতিযোগিতা ও পুরস্কার বিতরণ অনুষ্ঠান', '<div class="entry-inner">\n        <p >ঢাকার অদূরে মুন্সিগঞ্জ জেলাস্থ \nজামিয়া মাহমুদিয়া আশরাফিয়া সেরাজাবাদ মাদরাসায় ইসলামি সাধারণ জ্ঞান বিষয়ক \nকুইজ প্রতিযোগিতা অনুষ্ঠিত হয়। এতে ঢাকা ও মুন্সিগঞ্জ সহ দেশের বিভিন্ন \nঅঞ্চল থেকে প্রতিযোগিরা অংশগ্রহণ করে। গত ১৭ অক্টোবর ২০১৬ ঈসাব্দে সঠিক \nউত্তরদাতাদের মাঝে সুনির্দিষ্ট নীতিমালার ভিত্তিতে ২৩জন বিজয়ীকে বাছাই করা \nহয়। যাদের নগদ অর্থ সহ আকর্ষনিয় পুরস্কার দেয়া হয়েছে।</p>\n <p >বিজয়ীদের হাতে পুরস্কার তুলে দিয়েছেন, \nপীরে কামেল হযরত মাওলানা হাফীজুদ্দী দা.বা.(মুহতামিম সেরাজাবাদ মাদরাসা) \nপুরস্কার বিতরণকালে বিজয়ীরা ছাড়াও অন্যান্য প্রতিযোগিরা উপস্থিত ছিলেন। \nহযরত সকলের উদ্দেশ্যে অত্যান্ত মূল্যবান ও উৎসাহ ব্যঞ্চক আলোচনা পেশ করেন। \nযার ফলে যারা সেদিন বিজয়ী হতে পারেন নি, তারাও মনকষ্ট না পেয়ে সাধারণ জ্ঞান\n অর্জনের প্রতি আরো উৎসাহিত হয়েছে।</p>\n <p >আমরা আশা করি, আমাদের নতুন প্রজন্মকে \nইসলাম সম্পর্কে জানার আগ্রহ সৃষ্টি করতে মাদরাসা কর্তৃপক্ষ প্রতি বছরই \nএধরণের কুইজ প্রতিযোগিতার আয়োজন করবে। আল্লাহ তাআলা আমাদের সকলের প্রতি \nসহায় হোন। আমীন।</p>\n\n             </div>', 1),
(2, 'জামিয়ার নিজস্ব জায়গায় বালু ভরাটের কাজ চলছে', '<p >আল হামদুলিল্লাহ! জামিয়াতুল আস’আদ আল \r\nইসলামিয়ার নিজস্ব জায়গায় বালু ভরাটের কাজ চলছে। আল্লাহর রহমতে খিলগাও, \r\nনন্দিপাড়া, শেখের জায়গায় সাড়ে তিন কাঠা জায়গার ব্যবস্থা হয়েছে। ভবিষ্যতে এর\r\n পরিধি আরো বড় করার ইচ্ছা আছে। জামিয়ার সকল সুভাকাঙ্খী ও মুহিব্বিনদের \r\nজানাতে চাচ্ছি, যারা ইতিপূর্বে জামিয়ায় সহযোগিতার ইচ্ছা প্রকাশ করেছিলেন, \r\nএখন তাদের জন্য সুবর্ণ সুযোগ। জামিয়ার বালু ভরাটে অংশিদার হয়ে ‘সদকায়ে \r\nজারিয়ার’ একটি ভাগ আপনিও নিতে পারেন। বালু ভারট হলে অতিশিঘ্রই মাদরাসা \r\nভবনের কাজ শুরু হবে। ইনশা আল্লাহ।</p>\r\n<p>যোগাযোগের জন্য ফোন করুন,<br>\r\n০১৭৬২১২১০৯৩<br>\r\n০১৮১৮৭৮১৩৮৩<br>\r\n০১৮১৮৭৩২৪৬৫</p>', 1),
(3, 'রমজান উপলক্ষ্যে জামিয়া ছুটি ঘোষণা', 'পবিত্র্ রমজান, &nbsp;পরীক্ষা পরবর্তী জামিয়ার কিতাব বিভাগ &nbsp;13 শাবান শনিবার \r\nথেকে 7 শাওয়াল বুধবার পর্যন্ত ছুটি ঘোষণা করা হয়েছে। 8 শাওয়াল বৃহস্পতিবার \r\nপুরাতন ছাত্রদের ভর্তি কার্যক্রম শুরু হবে ইনশাআল্লাহ। এবং পরবর্তী 3 দিনে \r\nনতুন ছাত্রদের ভর্তি চলবে। &nbsp; এ বছর ভর্তি ফি 1800 টাকা ধার্য করা হয়েছে এবং\r\n আনুসাঙ্গিক খরচ সামান্য বৃদ্ধি &nbsp;হতে পারে যা পরবর্তীতে পোষ্টের মাধ্যমে \r\nজানানো হবে ইনশাআল্লাহ।', 1);

